import React, { useEffect, useState } from 'react';
import axios from 'axios';

const About = () => {
  const [info, setInfo] = useState(null);
  useEffect(() => {
    axios.get('http://localhost:5000/api/info/about').then(res => setInfo(res.data));
  }, []);
  return (
    <div className="p-8 max-w-2xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">About Us</h2>
      {info && (
        <>
          <p className="mb-2 font-semibold">{info.name}</p>
          <p className="mb-2">{info.description}</p>
          <p className="mb-2"><strong>Vision:</strong> {info.vision}</p>
          <p className="mb-2"><strong>Mission:</strong> {info.mission}</p>
        </>
      )}
    </div>
  );
};

export default About;
