import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Notes = () => {
  const [notes, setNotes] = useState([]);
  const [content, setContent] = useState('');
  const [error, setError] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) return;
    axios.get('http://localhost:5001/api/notes', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setNotes(res.data));
  }, [token]);

  const addNote = async (e) => {
    e.preventDefault();
    setError('');
    if (!content.trim()) return setError('Note content required');
    try {
      const res = await axios.post('http://localhost:5001/api/notes', { content }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes([res.data, ...notes]);
      setContent('');
    } catch (err) {
      setError('Failed to add note');
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5001/api/notes/${id}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setNotes(notes.filter(n => n._id !== id));
    } catch {
      setError('Failed to delete note');
    }
  };

  if (!token) {
    return (
      <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
        <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-md text-center">
          <h2 className="text-3xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">PasteApp Notes</h2>
          <p className="text-lg">You must be logged in to view and save notes.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-xl">
        <h2 className="text-3xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">PasteApp Notes</h2>
        <form onSubmit={addNote} className="flex gap-2 mb-6">
          <input
            type="text"
            value={content}
            onChange={e => setContent(e.target.value)}
            placeholder="Write your note..."
            className="flex-1 p-3 rounded bg-white/20 text-white placeholder-white/80 border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300"
          />
          <button type="submit" className="bg-yellow-300 text-blue-900 font-bold px-6 py-2 rounded-full shadow hover:bg-yellow-400 transition">Add</button>
        </form>
        {error && <div className="text-red-400 font-bold mb-4">{error}</div>}
        <ul className="space-y-4">
          {notes.map(note => (
            <li key={note._id} className="bg-blue-800/60 rounded px-4 py-3 shadow flex justify-between items-center">
              <span>{note.content}</span>
              <button onClick={() => deleteNote(note._id)} className="ml-4 bg-red-500 text-white px-3 py-1 rounded hover:bg-red-600 transition">Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Notes;
