import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Services = () => {
  const [services, setServices] = useState(null);
  useEffect(() => {
    axios.get('http://localhost:5000/api/info/services').then(res => setServices(res.data));
  }, []);
  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-3xl">
        <h2 className="text-4xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">Our Services</h2>
        {services && (
          <>
            <ul className="mb-6 grid grid-cols-2 gap-4 text-lg font-semibold">
              {services.services.map(s => <li key={s} className="bg-blue-800/60 rounded px-4 py-2 shadow">{s}</li>)}
            </ul>
            <div>
              <h3 className="font-bold text-xl mb-2 text-yellow-200">Details:</h3>
              <ul className="space-y-4">
                {Object.entries(services.details).map(([cat, items]) => (
                  <li key={cat} className="mb-2">
                    <span className="font-bold text-yellow-100">{cat.charAt(0).toUpperCase() + cat.slice(1)}:</span>
                    <ul className="list-disc pl-6">
                      {items.map(i => <li key={i} className="text-white/90">{i}</li>)}
                    </ul>
                  </li>
                ))}
              </ul>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Services;
