import React from 'react';

const Home = () => (
  <div className="min-h-[80vh] flex flex-col justify-center items-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
    <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-2xl text-center">
      <h1 className="text-5xl font-extrabold mb-6 drop-shadow-lg">Welcome to <span className="text-yellow-300">ZAMXTRON</span></h1>
      <p className="text-xl mb-4 font-medium">Your reliable technology partner for websites, apps, automation, and AI-powered platforms.</p>
      <p className="text-lg mb-6 italic">Empowering innovation and digital transformation.</p>
      <div className="flex justify-center gap-4 mt-6">
        <a href="/services" className="bg-yellow-300 text-blue-900 font-bold px-6 py-2 rounded-full shadow hover:bg-yellow-400 transition">Our Services</a>
        <a href="/contact" className="bg-white/20 text-white font-bold px-6 py-2 rounded-full shadow hover:bg-white/30 transition border border-white">Contact Us</a>
      </div>
    </div>
  </div>
);

export default Home;
