import React, { useEffect, useState } from 'react';
import axios from 'axios';

const About = () => {
  const [info, setInfo] = useState(null);
  useEffect(() => {
    axios.get('http://localhost:5000/api/info/about').then(res => setInfo(res.data));
  }, []);
  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-2xl">
        <h2 className="text-4xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">About Us</h2>
        {info && (
          <>
            <p className="mb-4 text-2xl font-bold">{info.name}</p>
            <p className="mb-4 text-lg font-medium">{info.description}</p>
            <p className="mb-2"><span className="font-bold text-yellow-200">Vision:</span> <span className="italic">{info.vision}</span></p>
            <p className="mb-2"><span className="font-bold text-yellow-200">Mission:</span> <span className="italic">{info.mission}</span></p>
          </>
        )}
      </div>
    </div>
  );
};

export default About;
