import React, { useEffect, useState } from 'react';
import axios from 'axios';

const UserProfile = () => {
  const [user, setUser] = useState(null);
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return;
    axios.get('http://localhost:5001/api/auth/profile', {
      headers: { Authorization: `Bearer ${token}` }
    }).then(res => setUser(res.data.user)).catch(() => setUser(null));
  }, []);
  if (!user) {
    return (
      <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
        <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-md text-center">
          <h2 className="text-3xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">User Profile</h2>
          <p className="text-lg">You are not logged in.</p>
        </div>
      </div>
    );
  }
  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-md text-center">
        <h2 className="text-3xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">User Profile</h2>
        <p className="text-xl font-bold mb-2">{user.name}</p>
        <p className="mb-2">Email: <span className="font-semibold">{user.email}</span></p>
        <p className="mb-2">Account Created: {new Date(user.createdAt).toLocaleString()}</p>
      </div>
    </div>
  );
};

export default UserProfile;
