import React, { useEffect, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';

const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) return setUser(null);
    fetch('http://localhost:5000/api/auth/profile', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => setUser(data.user))
      .catch(() => setUser(null));
  }, [location]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="bg-gradient-to-r from-blue-900 via-blue-700 to-blue-400 text-white px-8 py-4 flex justify-between items-center shadow-lg sticky top-0 z-50">
      <div className="font-extrabold text-2xl tracking-wide drop-shadow-lg">
        <span className="text-yellow-300">ZAMXTRON</span>
      </div>
      <div className="space-x-4 flex items-center">
        <Link to="/" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Home</Link>
        <Link to="/about" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">About</Link>
        <Link to="/services" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Services</Link>
        <Link to="/contact" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Contact</Link>
        {!user && <Link to="/login" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Login</Link>}
        {!user && <Link to="/signup" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Sign Up</Link>}
        {user && <Link to="/profile" className="px-4 py-2 rounded-full hover:bg-yellow-300 hover:text-blue-900 font-bold transition">Profile</Link>}
        <Link to="/pasteapp" className="px-4 py-2 rounded-full hover:bg-green-300 hover:text-blue-900 font-bold transition">PasteApp</Link>
        {user && (
          <span className="ml-4 font-bold text-yellow-200">{user.name}</span>
        )}
        {user && (
          <button onClick={handleLogout} className="ml-2 px-4 py-2 rounded-full bg-red-500 text-white font-bold hover:bg-red-600 transition">Logout</button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
