import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Contact = () => {
  const [contact, setContact] = useState(null);
  useEffect(() => {
    axios.get('http://localhost:5000/api/info/contact').then(res => setContact(res.data));
  }, []);
  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-xl">
        <h2 className="text-4xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">Contact Us</h2>
        {contact && (
          <div className="space-y-4 text-lg">
            <p><span className="font-bold text-yellow-200">Email:</span> {contact.email}</p>
            <p><span className="font-bold text-yellow-200">Phone:</span> {contact.phone}</p>
            <p><span className="font-bold text-yellow-200">Location:</span> {contact.location}</p>
            <p><span className="font-bold text-yellow-200">Instagram:</span> {contact.instagram}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Contact;
