import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Services = () => {
  const [services, setServices] = useState(null);
  useEffect(() => {
    axios.get('http://localhost:5000/api/info/services').then(res => setServices(res.data));
  }, []);
  return (
    <div className="p-8 max-w-3xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">Our Services</h2>
      {services && (
        <>
          <ul className="mb-4 list-disc pl-6">
            {services.services.map(s => <li key={s}>{s}</li>)}
          </ul>
          <div>
            <h3 className="font-semibold mt-4">Details:</h3>
            <ul className="list-disc pl-6">
              {Object.entries(services.details).map(([cat, items]) => (
                <li key={cat} className="mb-2">
                  <strong>{cat.charAt(0).toUpperCase() + cat.slice(1)}:</strong>
                  <ul className="list-disc pl-6">
                    {items.map(i => <li key={i}>{i}</li>)}
                  </ul>
                </li>
              ))}
            </ul>
          </div>
        </>
      )}
    </div>
  );
};

export default Services;
