import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
      localStorage.setItem('token', res.data.token);
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="min-h-[80vh] flex items-center justify-center bg-gradient-to-br from-blue-900 via-blue-700 to-blue-400 text-white">
      <div className="bg-white/10 backdrop-blur-lg rounded-xl shadow-lg p-10 w-full max-w-md">
        <h2 className="text-3xl font-extrabold mb-6 drop-shadow-lg text-yellow-300">Login</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
          <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} className="w-full p-3 rounded bg-white/20 text-white placeholder-white/80 border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300" required />
          <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} className="w-full p-3 rounded bg-white/20 text-white placeholder-white/80 border border-white/30 focus:outline-none focus:ring-2 focus:ring-yellow-300" required />
          {error && <div className="text-red-400 font-bold">{error}</div>}
          <button type="submit" className="bg-yellow-300 text-blue-900 font-bold px-6 py-2 rounded-full shadow hover:bg-yellow-400 transition w-full">Login</button>
          <div className="mt-2 text-center">
            <span className="text-gray-700">Don't have account? </span>
            <a href="/signup" className="text-blue-900 font-bold hover:underline">SIGN UP</a>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
